mapper.update:
UPDATE BOARD
	SET BOARD_STATUS ='N'
WHERE BOARD_NO = #{boardNo}

UPDATE REPLY
	SET REPLY_STATUS ='N',
		REPLY_MODIFY_DATE = SYSDATE
WHERE REPLY_BOARD_NO = #{replyBoardNo}
AND REPLY_NO = #{replyNo}

UPDATE REPLY
	SET REPLY_CONTENT =#{replyContent},
		REPLY_MODIFY_DATE = SYSDATE
WHERE REPLY_BOARD_NO = #{replyBoardNo}
AND REPLY_NO = #{replyNo}

mapper.select:
SELECT BOARD_NO, BOARD_TITLE, MEMBER_ID, BOARD_CONTENT, BOARD_CREATE_DATE
FROM BOARD JOIN MEMBER ON (BOARD_WRITER = MEMBER_NO)
WHERE BOARD_STATUS = 'Y' AND BOARD_NO = #{boardNo}

mapper.insert:
INSERT INTO REPLY ( REPLY_NO, REPLY_BOARD_NO, REPLY_WRITER, REPLY_CONTENT, REPLY_MODIFY_DATE
) VALUES( SEQ_RNO.NEXTVAL, #{replyBno}, #{replyWriter}, #{replyContent}, #{replyModifyDate}
)

mapper.select:
SELECT  BOARD_CONTENT
FROM 	BOARD   
WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE REPLY

SET REPLY_CONTENT = #{replyContent}

WHERE REPLY_BOARD_NO = #{replyBno}
