mapper.insert:
INSERT INTO TRIP_PLAN 
		(TRIP_PLAN_NO
		,TRIP_PLANNER_NO
		,TRIP_PLAN_TITLE
		,TRIP_START_DATE
		,TRIP_END_DATE
		,TRANSPORTATION_NO
		,TRIP_PLAN_THUMBNAIL
		)
VALUES (SEQ_TPNO.NEXTVAL
		,#{memberNo}
		,TO_CHAR(SYSDATE, 'YY/MM/DD') || '일정'
		,#{startDate}
		,#{endDate}
		,#{transportationNo}
		,'https://mobile.busan.com/nas/wcms/wcms_data/photos/2022/12/17/2022121712041864096_l.jpg'
					)

	SELECT SEQ_TPNO.currval FROM dual

mapper.update:
UPDATE BOARD
	SET BOARD_STATUS ='N'
WHERE BOARD_NO = #{boardNo}

UPDATE REPLY
	SET REPLY_STATUS ='N',
		REPLY_MODIFY_DATE = SYSDATE
WHERE REPLY_BOARD_NO = #{replyBoardNo}
AND REPLY_NO = #{replyNo}

UPDATE REPLY
	SET REPLY_CONTENT =#{replyContent},
		REPLY_MODIFY_DATE = SYSDATE
WHERE REPLY_BOARD_NO = #{replyBoardNo}
AND REPLY_NO = #{replyNo}

mapper.insert:
INSERT INTO REPLY ( REPLY_NO, REPLY_BOARD_NO, REPLY_WRITER, REPLY_CONTENT, REPLY_MODIFY_DATE
) VALUES( SEQ_RNO.NEXTVAL, #{replyBno}, #{replyWriter}, #{replyContent}, #{replyModifyDate}
)

mapper.select:
SELECT  BOARD_CONTENT
FROM 	BOARD   
WHERE BOARD_NO = #{boardNo}

select TRIP_PLAN_NO ,TRIP_PLAN_TITLE ,TRIP_START_DATE ,TRIP_END_DATE ,LOCATION_NO ,TRIP_PLAN_THUMBNAIL ,ATTRACTION_NO ,ATTRACTION_NAME ,ATTRACTION_CHANGE_NAME_IMG ,CATEGORY_NAME ,REGION_NO ,REGION_NAME ,TRIP_N_DAY FROM TRIP_PLAN
JOIN MEMBER ON (TRIP_PLANNER_NO = MEMBER_NO) JOIN LOCATION using (TRIP_PLAN_NO) JOIN ATTRACTION ON (LOCATION_ATTRACTION_NO = ATTRACTION_NO) JOIN CATEGORY ON (ATTRACTION_CATEGORY_NO = CATEGORY_NO) JOIN REGION ON(ATTRACTION_REGION_NO =REGION_NO)
WHERE TRIP_PLANNER_NO = #{memberNo}

