mapper.update:
UPDATE MEMBER
   SET 
      MEMBER_PROFILE_IMG = #{memberProfileImg}
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER
    SET MEMBER_STATUS = 'N'
WHERE MEMBER_ID = #{memberId}

UPDATE FRIEND
    SET FRIEND_STATUS = 'N'
WHERE FRIEND_MEMBER_NO = #{memberNo}

mapper.insert:
INSERT INTO MEMBER( MEMBER_NO, MEMBER_ID, MEMBER_PWD, MEMBER_NAME, MEMBER_NICKNAME, MEMBER_EMAIL, MEMBER_PHONE
) VALUES( SEQ_MNO.NEXTVAL, #{memberId}, #{memberPwd}, #{memberName}, #{memberNickName},
replace(#{memberEmail}, ',', '@'),
#{memberPhone}
)

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_NICKNAME = #{memberNickName}

SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_ID = #{memberId}
AND MEMBER_EMAIL = #{memberEmail}
